package com.example.demo.dao;

import com.example.demo.model.CFUStudents;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Modifying;
import org.springframework.data.jpa.repository.Query;
import org.springframework.transaction.annotation.Transactional;

public interface CFURepo extends JpaRepository<CFUStudents, Integer> {
    CFUStudents findByEmailStudent(String emailStudent);

    @Transactional
    @Modifying(clearAutomatically = true)
    @Query(value="update CFUStudents set currentCFU = ?1, confirmedLessons = ?2 where emailStudent = ?3"                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        )
    void updateStudent(int currentCFU, int confirmedLesson, String emailStudent);
}
